// Error Classification and Processing
const errorData = $input.all();

function classifyError(error) {
  const errorMessage = error.message?.toLowerCase() || '';
  const errorStack = error.stack || '';

  // Database errors
  if (errorMessage.includes('connection') || errorMessage.includes('database') || errorMessage.includes('sql')) {
    return {
      category: 'database',
      severity: 'high',
      icon: 'ðŸ—„ï¸',
      action: 'restart_database_connection'
    };
  }

  // API/External service errors
  if (errorMessage.includes('api') || errorMessage.includes('timeout') || errorMessage.includes('rate limit')) {
    return {
      category: 'external_api',
      severity: 'medium',
      icon: 'ðŸ”—',
      action: 'retry_with_backoff'
    };
  }

  // AI Service errors
  if (errorMessage.includes('openai') || errorMessage.includes('groq') || errorMessage.includes('claude')) {
    return {
      category: 'ai_service',
      severity: 'medium',
      icon: 'ðŸ¤–',
      action: 'fallback_ai_model'
    };
  }

  // File system errors
  if (errorMessage.includes('file') || errorMessage.includes('upload') || errorMessage.includes('storage')) {
    return {
      category: 'filesystem',
      severity: 'medium',
      icon: 'ðŸ“',
      action: 'check_disk_space'
    };
  }

  // Memory/Performance errors
  if (errorMessage.includes('memory') || errorMessage.includes('heap') || errorMessage.includes('timeout')) {
    return {
      category: 'performance',
      severity: 'high',
      icon: 'âš¡',
      action: 'restart_service'
    };
  }

  // Platform integration errors
  if (errorMessage.includes('youtube') || errorMessage.includes('tiktok') || errorMessage.includes('instagram')) {
    return {
      category: 'platform_integration',
      severity: 'medium',
      icon: 'ðŸ“±',
      action: 'refresh_tokens'
    };
  }

  // Authentication/Authorization errors
  if (errorMessage.includes('auth') || errorMessage.includes('token') || errorMessage.includes('unauthorized')) {
    return {
      category: 'authentication',
      severity: 'high',
      icon: 'ðŸ”',
      action: 'refresh_credentials'
    };
  }

  // Default classification
  return {
    category: 'unknown',
    severity: 'medium',
    icon: 'â“',
    action: 'manual_investigation'
  };
}

const processedErrors = errorData.map(error => {
  const classification = classifyError(error);

  return {
    ...error,
    classification,
    timestamp: new Date().toISOString(),
    error_id: `err_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    service: error.service || 'unknown',
    workflow: error.workflow || 'unknown',
    user_id: error.user_id || null,
    session_id: error.session_id || null,
    request_id: error.request_id || null
  };
});

return processedErrors;

