// Group uploads by platform
const uploads = $input.first().json;
const platformGroups = {};

uploads.forEach(upload => {
  if (!platformGroups[upload.platform]) {
    platformGroups[upload.platform] = [];
  }
  platformGroups[upload.platform].push(upload);
});

// Return array of platform groups
return Object.entries(platformGroups).map(([platform, uploads]) => ({
  json: {
    platform: platform,
    uploads: uploads,
    upload_count: uploads.length
  }
}));

// Process analytics data and store metrics
const analyticsData = $input.all();
const metricsToStore = [];

analyticsData.forEach(platformData => {
  if (platformData.json.metrics && Array.isArray(platformData.json.metrics)) {
    platformData.json.metrics.forEach(metric => {
      metricsToStore.push({
        upload_id: metric.upload_id,
        views: metric.views || 0,
        likes: metric.likes || 0,
        comments: metric.comments || 0,
        shares: metric.shares || 0,
        watch_time_minutes: metric.watch_time_minutes || 0,
        engagement_rate: metric.engagement_rate || 0,
        revenue: metric.revenue || 0,
        unique_viewers: metric.unique_viewers || 0,
        measured_at: new Date().toISOString(),
        platform: platformData.json.platform
      });
    });
  }
});

return metricsToStore.map(metric => ({ json: metric }));

// Detect performance alerts and anomalies
const metricsData = $input.all();
const alerts = [];
const now = new Date();

metricsData.forEach(item => {
  const metric = item.json;

  // Viral content detection (high views + engagement)
  if (metric.views > 100000 && metric.engagement_rate > 5) {
    alerts.push({
      type: 'viral_content',
      upload_id: metric.upload_id,
      platform: metric.platform,
      message: `ðŸš€ Content going VIRAL! ${metric.views.toLocaleString()} views with ${metric.engagement_rate.toFixed(1)}% engagement`,
      priority: 'high',
      metric: {
        views: metric.views,
        engagement_rate: metric.engagement_rate,
        likes: metric.likes,
        comments: metric.comments
      }
    });
  }

  // High engagement rate (even with lower views)
  if (metric.engagement_rate > 15 && metric.views > 1000) {
    alerts.push({
      type: 'high_engagement',
      upload_id: metric.upload_id,
      platform: metric.platform,
      message: `ðŸ’¬ High engagement content! ${metric.engagement_rate.toFixed(1)}% engagement rate`,
      priority: 'medium',
      metric: {
        views: metric.views,
        engagement_rate: metric.engagement_rate
      }
    });
  }

  // Low performance detection (content older than 24 hours with low views)
  // Note: This would need upload timestamp comparison in real implementation
  if (metric.views < 100) {
    alerts.push({
      type: 'low_performance',
      upload_id: metric.upload_id,
      platform: metric.platform,
      message: `âš ï¸ Content underperforming: Only ${metric.views} views`,
      priority: 'low',
      metric: {
        views: metric.views,
        engagement_rate: metric.engagement_rate
      }
    });
  }

  // Revenue milestone
  if (metric.revenue > 100) {
    alerts.push({
      type: 'revenue_milestone',
      upload_id: metric.upload_id,
      platform: metric.platform,
      message: `ðŸ’° Revenue milestone! $${metric.revenue.toFixed(2)} earned`,
      priority: 'medium',
      metric: {
        revenue: metric.revenue,
        views: metric.views
      }
    });
  }
});

// Return alerts as separate items
return alerts.map(alert => ({
  json: {
    ...alert,
    timestamp: now.toISOString(),
    alert_id: `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
  }
}));

// No uploads to monitor
return {
  status: 'info',
  message: 'No active uploads found for monitoring',
  timestamp: new Date().toISOString(),
  next_check: 'Next automated check in 1 hour'
};

// Generate monitoring summary
const alertsData = $('Detect Performance Alerts').all();
const metricsData = $('Store Performance Metrics').all();

// Count alerts by type
const alertCounts = {};
alertsData.forEach(alert => {
  const type = alert.json.type;
  alertCounts[type] = (alertCounts[type] || 0) + 1;
});

// Calculate totals
const totalViews = metricsData.reduce((sum, item) => sum + (item.json.views || 0), 0);
const totalRevenue = metricsData.reduce((sum, item) => sum + (item.json.revenue || 0), 0);
const avgEngagement = metricsData.length > 0 
  ? metricsData.reduce((sum, item) => sum + (item.json.engagement_rate || 0), 0) / metricsData.length 
  : 0;

return {
  monitoring_summary: {
    timestamp: new Date().toISOString(),
    uploads_monitored: metricsData.length,
    total_alerts: alertsData.length,
    alert_breakdown: alertCounts,
    performance_summary: {
      total_views: totalViews,
      total_revenue: totalRevenue.toFixed(2),
      avg_engagement_rate: avgEngagement.toFixed(1)
    },
    next_monitoring: 'Automated monitoring continues every hour'
  }
};

// Handle analytics API errors
const errorData = $input.first().json;

return {
  status: 'error',
  message: 'Analytics collection failed for some platforms',
  error: errorData.error || 'Unknown analytics error',
  timestamp: new Date().toISOString(),
  action: 'Monitoring will retry in next cycle'
};
